import style from './addStyle.module.css';
import React, { useState, useEffect } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { useNavigate } from 'react-router-dom';

export default function Add() {
  const login = useSelector((state) => state.logIn)
  const state = useSelector((state) => state)
  console.log('STATE==>', state);

  useEffect(() => {

    setName(login.name);

  }, [login.name])

  const [name, setName] = useState("");
  const [addres, setAddres] = useState("");
  const [comment, setComment] = useState("");

  const nameHandler = (e) => {
    setName(e.target.value);

  };
  const addresHandler = (e) => {
    setAddres(e.target.value);

  };
  const commentHandler = (e) => {
    setComment(e.target.value);

  };
  const navigate = useNavigate();

  const logInNow = async (e) => {
    e.preventDefault()
    try {
      if (password.length < 8 || name.length === 0) {
        setMessege("Пароль должен быть не менее 8 символов. Поле -ИМЯ- не может быть пустым.")
      } if (password.length >= 8 && name.length > 0) {
        e.preventDefault()
        dispatch(getLog(name, password));
      }  if (login.error) {
        setMessege(login.error)
      } 
    } catch (error) {

    }
  };


  return (
    <div className={style.box}>

      <form className={style.form}>

        <h2>Добавить заказ</h2>

        <div className={style.inputdiv}>
          <input
            className={style.inputs}
            type="text"
            placeholder="Ваше имя"
            value={name}
            onChange={(e) => nameHandler(e)}
          />
        </div>

        <div className={style.inputdiv}>
          <input
            className={style.inputs}
            type="text"
            placeholder="Ваш адрес"
            value={addres}
            onChange={(e) => addresHandler(e)}
          />
        </div>

        <div className={style.inputdiv}>
          <input
            className={style.inputs}
            type="text"
            placeholder="Коментарий"
            value={comment}
            onChange={(e) => commentHandler(e)}
          />
        </div>

        <button type='submit' className={style.button}>Добавить</button>

      </form>

    </div>
  )
}
